ALTER TABLE "delivery_tracking_logs" ADD FOREIGN KEY ("order_id") REFERENCES "orders" ("id");

ALTER TABLE "invoice_documents" ADD FOREIGN KEY ("order_id") REFERENCES "orders" ("id");

