ALTER TABLE "product_tag_map" ADD FOREIGN KEY ("product_id") REFERENCES "products" ("id");

ALTER TABLE "product_tag_map" ADD FOREIGN KEY ("tag_id") REFERENCES "product_tags" ("id");

