ALTER TABLE "bundle_items" ADD FOREIGN KEY ("bundle_id") REFERENCES "bundle_products" ("id");

ALTER TABLE "bundle_items" ADD FOREIGN KEY ("product_id") REFERENCES "products" ("id");

